//for sections that contain levels 5 & 6
function youAreHere1() {
	
	if (!document.getElementsByTagName) return false;
	if (!document.getElementById) return false;
	if (!document.getElementById("expand_energy_efficiency_nav")) return false;
	var nav = document.getElementById("expand_energy_efficiency_nav");

	var links = nav.getElementsByTagName("a");
	
	for (var i=0; i<links.length; i++) {
		var linkurl = links[i].getAttribute("href");
		var currenturl = window.location.href;
		if (currenturl.indexOf(linkurl) != -1) {
			links[i].className = "here";
		}
	}

}
$(document).ready(function() {youAreHere1();});

